import React, { useState } from "react";
import { FaRobot, FaPaperPlane } from "react-icons/fa";

const ChatWidget = () => {
  const [open, setOpen] = useState(false);
  const [msg, setMsg] = useState("");
  const [log, setLog] = useState([{ from: "bot", text: "Hi! Ask me about Anshika's work." }]);

  const send = () => {
    if (!msg.trim()) return;
    setLog([...log, { from: "you", text: msg }, { from: "bot", text: "Thanks! (Demo reply)" }]);
    setMsg("");
  };

  return (
    <>
      <div className="chat-fab" onClick={() => setOpen(!open)} title="Chat">
        <FaRobot size={22} color="#082a27" />
      </div>

      {open && (
        <div className="chat-panel">
          <div className="chat-header">Assistant</div>
          <div className="chat-body">
            {log.map((m, i) => (
              <div key={i} style={{ marginBottom: 10 }}>
                <b>{m.from === "you" ? "You" : "Bot"}:</b> <span>{m.text}</span>
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              value={msg}
              onChange={(e) => setMsg(e.target.value)}
              placeholder="Type a message..."
            />
            <button className="btn" onClick={send} title="Send">
              <FaPaperPlane />
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default ChatWidget;
